  const response = await MyAPI.run({
    'mac': mac
  });
  JS.upstreams = response.data.upstreams;
  JS.downstreams = response.data.downstreams;
  JS.cpes = response.data.cpes;
  JS.status = 'onlinie';
  JS.uptime = response.data.uptime;
  JS.course = 'ネット120M';
  JS.config = 'net-120m.cfg';
  JS.ip = '172.16.0.100';
}

