export default {
	status: '',
	uptime: '',
	course: '',
	config: '',
	ip: '',
	upstreams: [],
	downstreams: [],
	cpes: [],

	async executeMyAPI (mac = 'ee:ee:ee:00:00:00') {
		const response = await MyAPI.run({'mac': mac});
		this.upstreams   = response.data.upstreams;
		this.downstreams = response.data.downstreams;
		this.cpes        = response.data.cpes;
		this.status      = 'onlinie';
		this.uptime      = response.data.uptime;
		this.course      = 'ネット120M';
		this.config      = 'net-120m.cfg';
		this.ip          = '172.16.0.100';
	},
	async copyMyDataToClipboard() {
		const str = InputMac.text + ' uptime is ' + Uptime.text + '.';
		// https://docs.appsmith.com/reference/appsmith-framework/widget-actions/copy-to-clipboard
		copyToClipboard(str);
	}
}

